import React from 'react';
import './App.css';
import Project from './Components/Project';
import AddProject from './Components/AddProject';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      projects: []
    }
  }

handleAddProject() {
  //push the new project to the state
  //setState to update the state
}

  componentDidMount() {
    //run after output has been rendered, the output should be empty first
    //setState triggers the render() again, the list is shown
    this.setState({projects: [
      {
        title: 'Business website',
        category: 'Web Design'  
      },
      {
        title: 'Social App',
        category: 'Mobile Development'  
      },
      {
        title: 'Ecommerce Shopping Cart',
        category: 'Web Development'  
      }
      ]
    })
  }

  render() {
    return (
      <div className="App">
        <AddProject addProject={handleAddProject} />
        <Project project={this.state.projects} />      
      </div>
    );  
  }
}

export default App;
