import React from 'react';

class AddProject extends React.Component {
  constructor(props) {
    super()
    this.state = {
      newProject: {
        title: '',
        category: ''
      }
    }
  }
  static defaultProps = {
    categories: ['Web Design', 'Web Development', 'Mobile Development']
  }

  handleChange(e) {
    this.setState({
      newProject: {
        title: e.target.title.value,

      }
    })
  }


  handleSubmit(e) {
    //setState to update the newProject
    //pass the newProject to main app

    console.log(e.target.title.value);
    console.log(e.target.category.value);
    //prevent the refresh after the form is submitted
    e.preventDefault();
  }    

  render() {
    //map function put here to allow access from below
    let categoryOptions = this.props.categories.map(category => {
      return <option key={category} value={category} name={category}>{category}</option>
    });

    return (  
      <div>
        <h3>Add Projects</h3>
        <form onSubmit={this.handleSubmit.bind(this)} >
          <div>
            <label>Title</label>
            <input 
              name='title' 
              type='text' 
              value={this.state.newProject.title}  
              onChange={this.handleChange.bind(this)}
              required />
          </div>
          <div>
            <label>Category</label>
            <select 
              name='category'
              value={this.state.newProject.category}
              onChange={this.handleChange.bind(this)} >
              {categoryOptions}
            </select>
          </div>
          <input type='submit' value='submit' />
        </form>
      </div>
    );  
  }  	

}

export default AddProject;
