import React from 'react';
import uuid from 'uuid';
import './App.css';
import Project from './Components/Project';
import AddProject from './Components/AddProject';
import SearchBar from './Components/SearchBar';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      projects: [],
      fliterText: '',
      isCompleted: false
    }
    this.handleAddProject = this.handleAddProject.bind(this);
    this.deleteProject = this.deleteProject.bind(this);
  };

componentDidMount() {
  this.setState({projects: [
    {
      id: uuid.v4(),
      title: 'Business website',
      category: 'Web Design'  
    },
    { 
      id: uuid.v4(),
      title: 'Social App',
      category: 'Mobile Development'  
    },
    { 
      id: uuid.v4(),
      title: 'Ecommerce Shopping Cart',
      category: 'Web Development'  
    }
    ]
  })
}

deleteProject(id) {
  let projects = this.state.projects;
  let index = projects.findIndex((item) => item.id = id);
  projects.splice(index, 1);
  this.setState({projects: projects});  
}

handleAddProject(project) {
  let projects = this.state.projects;
  projects.push(project);
  this.setState({projects: projects});  
}

handleFilterText(filterText) {
  setState({filterText: filterText})
}

render() {
  return (
    <div className="container-main">
      <h3>KOOL Web & App Development</h3>
      <SearchBar 
        filterText={this.state.filterText}
        onFilterText={this.state.handleFilterText}
        onCompleted={this.state.isCompleted}   
      />
      <h3>Our projects</h3>
      <Project project={this.state.projects} onDelete={this.deleteProject}  />
      <AddProject addProject={this.handleAddProject} />      
    </div>
  );  
}
}

export default App;
