import React from 'react';

class AddProject extends React.Component {
  //props is not passed when function called. resort to the default props
  //only allowed in class component 
  static defaultProps = {
    categories: ['Web Design', 'Web Development', 'Mobile Development']
  }

  handleSubmit(e) {
    console.log(this.refs.title.value);
    //onsubmit - can it be put in input, not form?
    //prevent the refresh after the form is submitted
    e.preventDefault();
  }    

  render() {
    //map function put here to allow access from below
    let categoryOptions = this.props.categories.map(category => {
      return <option key={category} value={category} name={category}>{category}</option>
    });

    return (  
      <div>
        <h3>Add Projects</h3>
        <form onSubmit={this.handleSubmit.bind(this)}>
          <div>
            <label>Title</label>
            <input type='text' ref='title' />
          </div>
          <div>
            <label>Category</label>
            <select ref='category'>
              {categoryOptions}
            </select>
          </div>
          <input type='submit' value='submit' />
        </form>
      </div>
    );  
  }  	

}

export default AddProject;
