	console.log(id);
	this.props.onDelete(id);
  }

  render() {
  	return (	 
	  <li className="Project">
        {this.props.project.title}: {this.props.project.category}         
        <a href='#' onClick={this.onDelete} id={this.props.project.id} >X</a>
      </li>

	);
  }	
}

export default ProjectItem;
