import React from 'react';

class AddProject extends React.Component {
  constructor(props) {
    super()
    this.state = {
      newProject: {}
    }
  }
  static defaultProps = {
    categories: ['Web Design', 'Web Development', 'Mobile Development']
  }

  handleSubmit(e) {
    //the values in captured by form tag can be accessed by this.refs
    //setState to update the newProject
    //pass the newProject to main app

    console.log(this.refs.category.value);
    //prevent the refresh after the form is submitted
    e.preventDefault();
  }    

  render() {
    //map function put here to allow access from below
    let categoryOptions = this.props.categories.map(category => {
      return <option key={category} value={category} name={category}>{category}</option>
    });

    return (  
      <div>
        <h3>Add Projects</h3>
        <form onSubmit={this.handleSubmit.bind(this)} >
          <div>
            <label>Title</label>
            <input type='text' ref='title' required />
          </div>
          <div>
            <label>Category</label>
            <select ref='category'>
              {categoryOptions}
            </select>
          </div>
          <input type='submit' value='submit' />
        </form>
      </div>
    );  
  }  	

}

export default AddProject;
